<style lang="scss" scoped>
// mobile first
// footer
footer {
  // mobile 排版不顯示
  display: none;
}

//desktop layout
@media screen and (min-width: 376px) {
  footer {
    display: block;
    // 背景色
    background-color: var(--footer-background);
    .shoe {
      max-width: $view-width;
      margin: 0 auto;
    }

    .sock {
      padding: 64px #{percentage(165px / $view-width)} 80px;
    }

    h3.title {
      color: var(--main-text-black);
      font-size: 24px;
      line-height: 24px;
      margin-bottom: 24px;
    }
  }

  .subject-list {
    li {
      color: var(--footer-subject);
      margin-bottom: 8px;
      font-size: 16px;
      line-height: 17px;

      &:last-child {
        margin-bottom: 0px;
      }
      &:hover {
        color: var(--theme-pink);
      }
      &:focus {
        color: var(--theme-deep-pink);
      }
    }

    .phone {
      cursor: pointer;
      line-height: 16px;
    }

    .social {
      // 配合 dark mode控制 icon
      svg {
        path {
          fill: var(--social-icon);
        }
      }

      a {
        width: 24px;
        height: 24px;
        margin-right: 16px;
        &:last-child {
          margin-right: 0px;
        }
        &:hover {
          path {
            fill: var(--theme-pink);
          }
        }
      }
    }
  }
}
</style>
